-- Name: first_snap_to_grid(); Type: FUNCTION; Schema: ukos_base; Owner: -
--

CREATE FUNCTION ukos_base.first_snap_to_grid() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			rec 						RECORD;
			accuracy 				NUMERIC;
			table_schema 		CHARACTER VARYING = TG_TABLE_SCHEMA;
			table_name 			CHARACTER VARYING = TG_TABLE_NAME;
			current_query 	text = current_query();
			debug 					BOOLEAN = false;
		BEGIN
			-- Initialisieren
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Koordinatengenauigkeit' INTO accuracy;
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Funktion ukos_base.first_snap_to_grid im Trigger % on % aus.', TG_NAME, TG_TABLE_NAME; END IF;

			IF accuracy IS NOT NULL THEN
				-- Runde Koordinaten auf die Genauigkeit accuracy nach dem Komma
				FOR rec IN EXECUTE '
          SELECT
            g.f_geometry_column
          FROM
						geometry_columns g
						LEFT JOIN (
							SELECT
								n.nspname AS table_schema,
								c.relname as table_name,
								a.attname as column_name
							FROM pg_class c
								JOIN pg_inherits i on c.oid = i.inhrelid
								JOIN pg_attribute a on i.inhparent = a.attrelid
								JOIN pg_namespace n ON n.oid = c.relnamespace
							WHERE
								a.attnum > 0
						) inherited ON g.f_table_schema = inherited.table_schema AND g.f_table_name = inherited.table_name AND g.f_geometry_column = inherited.column_name
					WHERE
						g.f_table_schema = ' || quote_literal(table_schema) || ' AND
						g.f_table_name = ' || quote_literal(table_name) || ' AND
						inherited.column_name IS NULL
					ORDER BY
						g.f_table_schema,
						g.f_geometry_column
				'
				LOOP
					IF (hstore(NEW)->rec.f_geometry_column) IS NOT NULL THEN
						NEW = populate_record(NEW, (rec.f_geometry_column || ' => ' || ST_SnapToGrid(hstore(NEW)->rec.f_geometry_column, accuracy)::text)::hstore);
						PERFORM ukos_base.log(current_query::character varying, TG_NAME::character varying, TG_WHEN::character varying, TG_OP::character varying, format('%s.%s', TG_TABLE_SCHEMA, TG_TABLE_NAME)::character varying, format('SnapToGrid in column: %s', rec.f_geometry_column)::text);
						IF debug THEN RAISE NOTICE 'Koorigiere Geometrie in Spalte % auf %', rec.f_geometry_column, ST_AsText(ST_SnapToGrid(hstore(NEW)->rec.f_geometry_column, accuracy)); END IF;
					END IF;
				END LOOP;
			END IF;
			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW', TG_NAME, TG_TABLE_NAME; END IF;
			RETURN NEW;
		END;
	$_$;


