CREATE FUNCTION ukos_okstra.add_verbindungspunkte() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			uuid_vp										character varying;
			vp_beginnt_bei_geometrie	public.geometry(Point, 25833) = ST_StartPoint(NEW.liniengeometrie);
			vp_endet_bei_geometrie		public.geometry(Point, 25833) = ST_EndPoint(NEW.liniengeometrie);
			rec												RECORD;
			tolerance									NUMERIC;
			liniengeometrie_changed		BOOLEAN;
			beginnt_bei_vp_unbekannt 	BOOLEAN = (NEW.beginnt_bei_vp = '00000000-0000-0000-0000-000000000000');
			endet_bei_vp_unbekannt		BOOLEAN = (NEW.endet_bei_vp = '00000000-0000-0000-0000-000000000000');
			sql 											TEXT;
			msg 											TEXT[];
			debug 										BOOLEAN = false;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			-- Initialisierung
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF debug THEN RAISE NOTICE 'Füge Verindungspunkte des Strassenelementes mit Liniengeometrie: % und Topologietolerance: % hinzu,
			falls noch nicht übergeben oder nicht vorhanden und setze ggf. neue Geometrie und Verindungspunkte in Strassenelement ein.', ST_AsText(NEW.liniengeometrie), tolerance; END IF;

			--------------------------------------------------------------------------------------------------------
			IF beginnt_bei_vp_unbekannt THEN
				EXECUTE '
					SELECT id, ident, punktgeometrie, ST_Equals($1, punktgeometrie) punkt_gleich
					FROM ukos_okstra.verbindungspunkt
					WHERE ST_Distance(punktgeometrie, $1) < $2
					ORDER BY ST_Distance(punktgeometrie, $1)
					LIMIT 1
				'
				USING vp_beginnt_bei_geometrie, tolerance
				INTO rec;

				IF rec.id IS NULL THEN --wenn kein Knoten an der Stelle gefunden wird
					uuid_vp = uuid_generate_v4(); --dann uuid neu generieren
					RAISE NOTICE 'Kein Verbindungspunkt (VP) am Anfang des Strassenelementes gefunden. Lege neuen VP an mit id: %', uuid_vp;
					EXECUTE FORMAT('INSERT INTO ukos_okstra.verbindungspunkt (id, id_strasse, punktgeometrie, angelegt_von) VALUES ( %1$L, %2$L, %3$L, %4$L)', uuid_vp, NEW.id_strasse, vp_beginnt_bei_geometrie, NEW.angelegt_von); --und Datensatz anlegen
					NEW.beginnt_bei_vp = uuid_vp; --und die id im neuen strassenelement eintragen
				ELSE
					msg = array_append(msg, FORMAT('Verbindungspunkt: %s am Anfang des Strassenelementes gefunden.', rec.ident));
					-- Die id des gefundenen Verbindungspunktes übernehmen
					NEW.beginnt_bei_vp = rec.id;
					IF NOT rec.punkt_gleich THEN
						IF debug THEN RAISE NOTICE 'Übernehme die Koordinaten des gefundenen Anfangspunktes % (%)', rec.id, ST_AsText(rec.punktgeometrie);
						NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, 0, rec.punktgeometrie); END IF;
						liniengeometrie_changed = true;
					END IF;
				END IF;
			ELSE
				msg = array_append(msg, FORMAT('Verbindungspunkt am Anfang ist mit übergeben worden: %s', NEW.beginnt_bei_vp));
			END IF;

			--------------------------------------------------------------------------------------------------------
			IF endet_bei_vp_unbekannt THEN
				EXECUTE '
					SELECT id, ident, punktgeometrie, ST_Equals($1, punktgeometrie) punkt_gleich
					FROM ukos_okstra.verbindungspunkt WHERE ST_Distance(punktgeometrie, $1) <= $2
					ORDER BY ST_Distance(punktgeometrie, $1)
					LIMIT 1
				'
				USING vp_endet_bei_geometrie, tolerance
				INTO rec;

				IF rec.id IS NULL THEN --wenn kein Knoten an der Stelle gefunden wird
					uuid_vp = uuid_generate_v4(); --dann uuid neu generieren
					RAISE NOTICE 'Kein Verbindungspunkt (VP) am Ende des Strassenelementes gefunden. Lege neuen VP an mit id: %', uuid_vp;
					EXECUTE FORMAT('INSERT INTO ukos_okstra.verbindungspunkt (id, id_strasse, punktgeometrie, angelegt_von) VALUES ( %1$L, %2$L, %3$L, %4$L)', uuid_vp, NEW.id_strasse, vp_endet_bei_geometrie, NEW.angelegt_von); --und Datensatz anlegen
					NEW.endet_bei_vp = uuid_vp; --und die id im neuen strassenelement eintragen
				ELSE
					msg = array_append(msg, FORMAT(' Verbindungspunkt: %s am Ende des Strassenelementes gefunden.', rec.ident));
					-- Die id des gefundenen Verbindungspunktes übernehmen
					NEW.endet_bei_vp = rec.id;
					IF NOT rec.punkt_gleich THEN
						IF debug THEN RAISE NOTICE 'Übernehme die Koordinaten des gefundenen Endpunktes % (%)', rec.id, ST_AsText(rec.punktgeometrie); END IF;
						NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, ST_NumPoints(NEW.liniengeometrie) - 1, rec.punktgeometrie);
						liniengeometrie_changed = true;
					END IF;
				END IF;
			ELSE
				msg = array_append(msg, FORMAT('Verbindungspunkt am Ende ist mit übergeben worden: %s', NEW.endet_bei_vp));
			END IF;

			--------------------------------------------------------------------------------------------------------
			IF beginnt_bei_vp_unbekannt OR endet_bei_vp_unbekannt THEN
				sql = '
					UPDATE
						ukos_okstra.strassenelement
					SET
						beginnt_bei_vp = ' || quote_literal(NEW.beginnt_bei_vp) || ',
						endet_bei_vp = ' || quote_literal(NEW.endet_bei_vp) || '
				';
				IF liniengeometrie_changed THEN
					sql = sql || ', liniengeometrie = ' || quote_literal(NEW.liniengeometrie::text);
				END IF;
				sql = sql || ' WHERE id = ' || quote_literal(NEW.id);
				--RAISE NOTICE 'Update Anfang, Endpunkt und ggf. Liniengeometrie von Strassenelement mit SQL: %', sql;
				EXECUTE sql;
			END IF;

			RAISE NOTICE '%', array_to_string(msg, '<p>');

			RETURN NEW;
		END;
	$_$;


