--
-- Name: change_strassenelemente(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.change_strassenelemente() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			tolerance 			NUMERIC;
			aenderungszeit 	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			edge 						RECORD;
			sql 						TEXT;
			debug 					BOOLEAN = true;
		BEGIN

			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			-- Initialisierung
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF debug THEN RAISE NOTICE 'Erster After Update Trigger alte Punktgeometrie: %, neue Punktgeometrie: %', ST_AsText(OLD.punktgeometrie), ST_AsText(NEW.punktgeometrie); END IF;
			IF ST_Distance(OLD.punktgeometrie, NEW.punktgeometrie) > tolerance THEN
				-- neuer Punkt außerhalb der tolerance
				-- finde alle anhängigen Strassenelemente und starte deren Update mit geänderter Geometrie am Anfangs- oder Endpunkt

				IF debug THEN RAISE NOTICE 'Schalte Trigger tr_after_update_update_topogeom und tr_before_update_10_or_insert_snap_to_net on strassenelement aus.'; END IF;
				EXECUTE 'ALTER TABLE ukos_okstra.strassenelement DISABLE trigger tr_after_update_update_topogeom';
				EXECUTE 'ALTER TABLE ukos_okstra.strassenelement DISABLE trigger tr_before_update_10_or_insert_snap_to_net';

				-- Frage edges der anhängigen Strassenelemente, entferne die Topoelements von Topogeometrie und die Edges von der Topologie
				sql = FORMAT('
					SELECT
						(topology.GetTopoGeomElements(liniengeometrie_topo))[1] AS id,
						topology.TopoGeom_remElement(liniengeometrie_topo, topology.GetTopoGeomElements(liniengeometrie_topo))
					FROM
						ukos_okstra.strassenelement
					WHERE
						gueltig_bis > %1$L AND
						(beginnt_bei_vp = %2$L OR endet_bei_vp = %2$L)
					',
					aenderungszeit, NEW.id
				);
				FOR edge IN EXECUTE sql LOOP
					EXECUTE FORMAT('SELECT topology.ST_RemEdgeModFace(%1$L, %2$s)', 'ukos_topo', edge.id);
				END LOOP;

				-- Passe Geometrie der anhängenden SE mit der Geom des VP an.
				-- Call ST_SetPoint two times, because verbindungspunkt can be start and end point of strassenelement
				sql = FORMAT('
					UPDATE
						ukos_okstra.strassenelement
					SET
						liniengeometrie = ST_SetPoint(liniengeometrie, CASE WHEN beginnt_bei_vp = %3$L THEN 0 ELSE -1 END, %4$L)
					WHERE
						gueltig_bis > %2$L AND
						(beginnt_bei_vp = %3$L OR endet_bei_vp = %3$L)
				', 'ukos_topo', aenderungszeit, NEW.id, NEW.punktgeometrie);
				EXECUTE sql;

				-- Remove isolated Nodes
				EXECUTE '
					SELECT topology.ST_RemoveIsoNode(' || quote_literal('ukos_topo') || ', node_id)
					FROM ukos_topo.node
					WHERE containing_face = 0
				';

				-- Add new se to topology
				EXECUTE '
					UPDATE
						ukos_okstra.strassenelement
					SET
						liniengeometrie_topo = topology.toTopoGeom(liniengeometrie, ' || quote_literal('ukos_topo') || ', 1, $3)
					WHERE
						gueltig_bis > $1 AND
						beginnt_bei_vp = $2 OR endet_bei_vp = $2
				'
				USING aenderungszeit, NEW.id, tolerance;

				IF debug THEN RAISE NOTICE 'Schalte Trigger tr_after_update_update_topogeom und tr_before_update_10_or_insert_snap_to_net on strassenelement wieder ein.'; END IF;
				EXECUTE 'ALTER TABLE ukos_okstra.strassenelement ENABLE trigger tr_after_update_update_topogeom';
				EXECUTE 'ALTER TABLE ukos_okstra.strassenelement ENABLE trigger tr_before_update_10_or_insert_snap_to_net';

			ELSE
				-- neuer Punkt im Umkreis von tolerance
				-- führe normales Update aus mit alter Geometrie (Geometrie bleibt erhalten), Node sowie anhängende Strassenelemente und deren Kanten in der Topologie bleiben erhalten
				NEW.punktgeometrie = OLD.punktgeometrie;
			END IF;

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;

			RETURN NEW;
		END;
	$_$;


--
-- Name: check_abhaengigkeiten_strassenelementpunkt(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.check_abhaengigkeiten_strassenelementpunkt() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			anzahl_te INTEGER;
			anzahl_sap INTEGER;
			aenderungszeit TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
		BEGIN
			--------------------------------------------------------------------------------------------------------
			-- Frage Abhängigkeiten von noch lebenden Teilelementen ab
			EXECUTE '
				SELECT
					count(*)
				FROM
				  ukos_okstra.teilelement te
				WHERE
					gueltig_bis > $1 AND
					(
						beginnt_bei_strassenelempkt = $2 OR
						endet_bei_strassenelempkt = $2
					)
			'
			USING aenderungszeit, OLD.id
			INTO anzahl_te;
			IF anzahl_te > 0 THEN
				RETURN NULL; -- Abbruch des Löschvorgangs der Strassenelemente
			END IF;
			--------------------------------------------------------------------------------------------------------
			-- Frage Abhängigkeiten von noch lebenden Strassenausstattungspunkten ab
			EXECUTE '
				SELECT
					count(*)
				FROM
					ukos_okstra.strassenausstattung_punkt
				WHERE
					gueltig_bis > $1 AND
					bei_strassenelementpunkt_id = $2
			'
			USING aenderungszeit, OLD.id
			INTO anzahl_sap;
			IF anzahl_sap > 0 THEN
				RETURN NULL; -- Abbruch des Löschvorgangs des Strassenelementes
			END IF;

		RETURN OLD; -- Weiter mit Löschvorgang des Strassenelementes
	END;
	$_$;


