-- Name: create_with_sep(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.create_with_sep() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			rec								RECORD;
			aenderungszeit		TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			tolerance 				NUMERIC;
			sql								TEXT;
      se_id							CHARACTER VARYING;
      sep 	 						RECORD;
			ident 						character varying;
			target_table_name character varying;
			target_columns    character VARYING[];
      msg								TEXT[];
			oid								bigint;
			debug 						BOOLEAN = false;
		BEGIN

			-- Initialisieren
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;
			EXECUTE FORMAT('
				SELECT SUBSTRING(%1$L, 5)
			', TG_TABLE_NAME)
			INTO target_table_name;

			IF (NEW.bei_strassenelementpunkt_id IS NULL) THEN
				-- Es gibt hier garantiert eine Punktgeometrie wenn der Trigger tr_instead_10_insert_or_update_validate_sap ausgeführt wurde
				-- Finde heraus ob an punktgeometrie schon ein SEP existiert
				sql = FORMAT('
					SELECT
						id, ident
					FROM
						ukos_okstra.strassenelementpunkt
					WHERE ST_DWithin(punktgeometrie, %1$L, %2$s)
					LIMIT 1
				', NEW.punktgeometrie, tolerance);
				IF debug THEN RAISe NOTICE 'Suche SEP mit Sql: %', sql; END IF;
				EXECUTE sql INTO sep;

				IF (sep IS NULL) THEN
					-- Wenn kein SEP existiert, erzeuge neuen SEP an punktgeometrie
					IF debug THEN RAISe NOTICE 'Kein SEP gefunden'; END IF;
					sql = FORMAT('
						INSERT INTO ukos_okstra.strassenelementpunkt (punktgeometrie, angelegt_am, angelegt_von)
						VALUES(%1$L, %2$L, %3$L)
						RETURNING id
					', NEW.punktgeometrie, aenderungszeit, NEW.angelegt_von);
					EXECUTE sql
					INTO NEW.bei_strassenelementpunkt_id;
					IF debug THEN RAISe NOTICE 'Neuen SEP mit id: % angelegt.', NEW.bei_strassenelementpunkt_id; END IF;
					msg = array_append(msg, FORMAT('Strassenelementpunkt %1$s erzeugt.', NEW.bei_strassenelementpunkt_id));
				ELSE
					IF debug THEN RAISe NOTICE 'SEP mit ident: % gefunden', sep.ident; END IF;
					msg = array_append(msg, FORMAT('Objekt fällt auf Strassenelementpunkt %1$s.', sep.ident));
					NEW.bei_strassenelementpunkt_id = sep.id;
				END IF;
			ELSE
				-- ignoriere die Koordinaten und weise einfach die strassenelement_id zu.
			END IF;

			sql = FORMAT('
				SELECT
					column_name
				FROM
					information_schema.columns
				WHERE
					table_schema = %1$L AND
					table_name = %2$L AND
					column_name NOT IN (''id'')
				ORDER BY
					column_name
			', TG_TABLE_SCHEMA, target_table_name);
			IF debug THEN RAISe NOTICE 'Frage alle Attribute der Doppik-Tabelle: %.% ab mit sql: %', TG_TABLE_SCHEMA, target_table_name, sql; END IF;
			IF debug THEN RAISe NOTICE 'Erzeuge neuen Datensatz in Tabelle: %.% mit folgenden Attributen', TG_TABLE_SCHEMA, target_table_name; END IF;
			FOR rec IN EXECUTE sql
			LOOP
				--IF debug THEN RAISe NOTICE '%: %', rec.column_name, hstore(NEW)->rec.column_name; END IF;
				IF (hstore(NEW)->rec.column_name) IS NOT NULL THEN
					IF debug THEN RAISe NOTICE '%: %', rec.column_name, hstore(NEW)->rec.column_name; END IF;
					target_columns = array_append(target_columns, rec.column_name::character VARYING);
				END IF;
      END LOOP;

			sql = FORMAT('
				INSERT INTO %1$I.%2$I (%3$s)
				SELECT %3$s
				', TG_TABLE_SCHEMA, target_table_name, array_to_string(target_columns,', '));

			RAISE NOTICE 'sql zum Anlegen des Objektes: %', sql;

			EXECUTE sql || ' FROM (SELECT ($1).*) AS foo RETURNING oid'
			INTO oid
			USING NEW;

			EXECUTE FORMAT('SELECT ident FROM %1$I.%2$I WHERE oid = %3$s', TG_TABLE_SCHEMA, target_table_name, oid)
			INTO ident;
			msg = array_append(msg, FORMAT('Punktobjekt %1$s erzeugt.', ident));

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
			RAISE NOTICE '{ "oid" : %, "msg": "%"}', oid, array_to_string(msg, '<br>');
			RETURN NEW;
		END;
	$_$;


--
-- Name: delete_punkte(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.delete_punkte() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			anzahl_strassenelemente	INTEGER;
			loeschsperre 							BOOLEAN;
			debug 										BOOLEAN = false;
		BEGIN
			-- Initialisiere
			EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Funktion ukos_okstra.delete_punkte in Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			IF loeschsperre THEN
				-- Verbinsungspunkt am Anfang löschen
				IF OLD.beginnt_bei_vp != '00000000-0000-0000-0000-000000000000' THEN
					IF debug THEN RAISE NOTICE 'Versuche Verbindungspunkt am Anfang: % zu löschen.', OLD.beginnt_bei_vp; END IF;
					EXECUTE '
						DELETE FROM ukos_okstra.verbindungspunkt
						WHERE id = $1
					'
					USING OLD.beginnt_bei_vp;
				END IF;
				--------------------------------------------------------------------------------------------------------
				-- Verbindungspunkt am Ende löschen
				IF OLD.endet_bei_vp != '00000000-0000-0000-0000-000000000000' THEN
					IF debug THEN RAISE NOTICE 'Versuche Verbindungspunkt am Ende: % zu löschen.', OLD.endet_bei_vp; END IF;
					EXECUTE '
						DELETE FROM ukos_okstra.verbindungspunkt
						WHERE id = $1
					'
					USING OLD.endet_bei_vp;
				END IF;

			END IF;
			--------------------------------------------------------------------------------------------------------
			-- Strassenelementpunkte zurücksetzen
			EXECUTE '
				UPDATE
					ukos_okstra.strassenelementpunkt
				SET
					auf_strassenelement = ''00000000-0000-0000-0000-000000000000''
				WHERE
					auf_strassenelement = $1
			'
			USING OLD.id;
			IF debug THEN RAISE NOTICE 'Strassenelement: % in Strassenelementpunkten zurückgesetzt.', OLD.id; END IF;
			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN OLD', TG_NAME, TG_TABLE_NAME; END IF;
		RETURN OLD;
	END;
	$_$;


