--
-- Name: delete_teilelemente(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.delete_teilelemente() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			te RECORD;
			aenderungszeit TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
		BEGIN
			--------------------------------------------------------------------------------------------------------
			-- Frage Teilelemente des Streckenobjektes ab, die nicht mehr von anderen abhängen und starte die Löschung
			FOR te IN EXECUTE '
				SELECT
					s2t.teilelement_id,
					s2t.streckenobjekt_id,
				  andere_te.teilelement_id AS id
				FROM
					ukos_okstra.teilelement te JOIN
					ukos_okstra.streckenobjekt_to_teilelement s2t ON te.id = s2t.teilelement_id LEFT JOIN
					(
						SELECT
							s2t2.teilelement_id,
							s2t2.streckenobjekt_id
						FROM
							ukos_okstra.teilelement te JOIN
							ukos_okstra.streckenobjekt_to_teilelement s2t2 ON te.id = s2t2.teilelement_id JOIN
							ukos_base.streckenobjekt so ON s2t2.streckenobjekt_id = so.id
						WHERE
							te.gueltig_bis > $1 AND
							so.gueltig_bis > $1 AND
							so.id != $2
					) andere_te ON s2t.teilelement_id = andere_te.teilelement_id
				WHERE
					te.gueltig_bis > $1 AND
					s2t.streckenobjekt_id = $2 AND
				  andere_te.teilelement_id IS NULL
				'
				USING aenderungszeit, OLD.id
			LOOP
				EXECUTE '
					DELETE FROM ukos_okstra.teilelement WHERE id = $1
				'
				USING te.teilelement_id;
			END LOOP;

			IF TG_OP = 'DELETE' THEN
				RETURN OLD;
			ELSE
				RETURN NEW;
			END IF;
	END;
	$_$;


--
-- Name: delete_topogeom(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.delete_topogeom() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			edge_id 			INTEGER;
			loeschsperre 	BOOLEAN;
			debug 				BOOLEAN = false;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
			IF debug THEN RAISE NOTICE 'Führe Funktion ukos_okstra.delete_topogeom in Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			IF debug THEN RAISE NOTICE 'Frage TopoGeomElement des Objektes: % ab.', OLD.id; END IF;
			EXECUTE '
				SELECT
					e[1]
				FROM
					(
						SELECT
							topology.GetTopoGeomElements(liniengeometrie_topo) e
						FROM
							ukos_okstra.strassenelement
						WHERE
							id = $1
						LIMIT 1
					) e_tab
			'
			USING OLD.id
			INTO edge_id;

			IF loeschsperre THEN
				IF debug THEN RAISE NOTICE 'Setze TopoGeom des Objektes: % zurück.', OLD.id; END IF;
				EXECUTE '
					UPDATE
						ukos_okstra.strassenelement
					SET
						liniengeometrie_topo = topology.clearTopoGeom(liniengeometrie_topo)
					WHERE
						id = $1
				'
				USING OLD.id;
			ELSE
				IF debug THEN RAISE NOTICE 'Lösche TopoGeom des Objektes: % in Relations.', OLD.id; END IF;
				EXECUTE FORMAT('
					SELECT
						topology.clearTopoGeom(liniengeometrie_topo)
					FROM
						ukos_okstra.strassenelement
					WHERE
						id = %1$L
				', OLD.id);
			END IF;

			IF edge_id IS NOT NULL THEN
				IF debug THEN RAISE NOTICE 'Entferne Edge: %', edge_id; END IF;
				EXECUTE '
					SELECT topology.ST_RemEdgeModFace(' || quote_literal('ukos_topo') || ', $1)
				'
				USING edge_id;
			END IF;

			IF debug THEN RAISE NOTICE 'Entferne isolierte Punkte.'; END IF;
			EXECUTE '
				SELECT
					topology.ST_RemoveIsoNode(' || quote_literal('ukos_topo') || ', node_id)
				FROM
					ukos_topo.node
				WHERE
					containing_face = 0
			';

			RETURN OLD;
		END;
	$_$;


--
-- Name: delete_with_sep(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.delete_with_sep() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			rec								RECORD;
			aenderungszeit		TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			tolerance 				NUMERIC;
			sql								TEXT;
      se_id							CHARACTER VARYING;
      sep 	 						RECORD;
			num_sap 					integer;
			updated						BOOLEAN = false;
			ident 						character varying;
			target_table_name character varying;
			target_columns    character VARYING[];
      msg								TEXT[];
			oid								bigint;
			debug 						BOOLEAN = false;
		BEGIN

			-- Initialisieren
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;
			EXECUTE FORMAT('
				SELECT SUBSTRING(%1$L, 5)
			', TG_TABLE_NAME)
			INTO target_table_name;

			-- Führe den DELETE Befehl on SAP aus!
			sql = FORMAT('
				DELETE FROM %1$I.%2$I
				WHERE id = %3$L
			', TG_TABLE_SCHEMA, target_table_name, OLD.id);
			IF debug THEN RAISE NOTICE 'Sql zum Löschen des Objektes: %', sql; END IF;
			EXECUTE sql;
			msg = array_append(msg, FORMAT('Punktobjekt %1$s gelöscht.', OLD.ident));

			-- Lösche auch SEP wenn kein anderer mehr dran hängt.
			IF (OLD.bei_strassenelementpunkt_id IS NOT NULL) THEN
				IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id existiert im zu löschenden Datensatz.'; END IF;
				sql = FORMAT('
					SELECT
						count(bei_strassenelementpunkt_id)
					FROM
						ukos_okstra.strassenausstattung_punkt
					WHERE
						gueltig_bis > %1$L AND
						bei_strassenelementpunkt_id = %2$L
				', aenderungszeit, OLD.bei_strassenelementpunkt_id);
				EXECUTE sql
				INTO num_sap;
				IF (num_sap = 0) THEN
					IF debug THEN RAISE NOTICE 'Es hängt kein SAP mehr an dem SEP: %.', OLD.bei_strassenelementpunkt_id; END IF;
					sql = FORMAT('
						DELETE FROM
							ukos_okstra.strassenelementpunkt
						WHERE
							id = %1$L
					', OLD.bei_strassenelementpunkt_id);
					IF debug THEN RAISE NOTICE 'SEP: % gelöscht mit sql %.', OLD.bei_strassenelementpunkt_id, sql; END IF;
					EXECUTE sql;
					msg = array_append(msg, FORMAT('Strassenelementpunkt %1$s wurde mit gelöscht.', OLD.bei_strassenelementpunkt_id));
				END IF;
			END IF;

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
			RAISE NOTICE '{ "oid" : %, "success" : true, "msg": "%"}', OLD.oid, array_to_string(msg, '<br>');

			RETURN OLD;
		END;
	$_$;


--
-- Name: FUNCTION delete_with_sep(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.delete_with_sep() IS 'Löscht den Strassenausstattungspunkt und ggf. wenn an dem SEP nichts anderes mehr mit drahängt auch den SEP.';


--
-- Name: liniengeometrie_aendern(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.liniengeometrie_aendern() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			vp_geom	public.geometry(Point, 25833);
			se_beginnt_bei_geom	public.geometry(Point, 25833) = ST_StartPoint(NEW.liniengeometrie);
			se_endet_bei_geom		public.geometry(Point, 25833) = ST_EndPoint(NEW.liniengeometrie);
			aenderungszeit	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			sql							TEXT;
			debug 					BOOLEAN = false;
		BEGIN

			-- Initialisieren
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			-- Prüfe ob sich am Anfang oder Ende etwas geändert hat
			IF ST_Equals(ST_StartPoint(OLD.liniengeometrie), ST_StartPoint(NEW.liniengeometrie)) AND
				ST_Equals(ST_EndPoint(OLD.liniengeometrie), ST_EndPoint(NEW.liniengeometrie)) THEN
				IF debug THEN RAISE NOTICE 'Anfangs- und Endpunkgeometrie sind gleich geblieben.'; END IF;
				-- Anfangs- und Endpunktgeometrie sind gleich geblieben

				-- Änderungszeit anpassen
				NEW.geaendert_am = aenderungszeit;

				-- ToDo:
				-- Prüfen ob bei einer Änderung der Sachdaten und gleicher Geometrie am Anfang und Ende das Strassenelement auch gelöscht und neu angelegt werden soll an Stelle eines Updates.
			ELSE
				IF debug THEN RAISE NOTICE 'Anfangs- oder Endpunkgeometrie hat sind geändert.'; END IF;
				-- Anfangs- oder Endpunktgeometrie hat sich geändert
				IF NOT ST_Equals(ST_StartPoint(OLD.liniengeometrie), se_beginnt_bei_geom) THEN
					IF debug THEN RAISE NOTICE 'Anfangspunkt der Linie hat sich geändert.'; END IF;
					IF NEW.beginnt_bei_vp IS NOT NULL THEN
						if debug THEN RAISE NOTICE 'VP am Anfang des SE wurde übergeben. Frage punktgeometrie vom VP ab.'; END IF;
						sql = FORMAT('SELECT punktgeometrie FROM ukos_okstra.verbindungspunkt WHERE id = %1$L', NEW.beginnt_bei_vp);
						EXECUTE sql INTO vp_geom;
						if debug THEN RAISE NOTICE 'Vergleiche VP Geometrie % mit Anfangspunkt der Linie %', ST_AsText(vp_geom), ST_AsText(se_beginnt_bei_geom); END IF;
						IF NOT ST_Equals(vp_geom, se_beginnt_bei_geom) THEN
							if debug THEN RAISE NOTICE 'Geometrie vom VP stimmt nicht mit Anfang des SE überein. Korrigiere punktgeometrie von VP am Anfang.'; END IF;
							sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET punktgeometrie = %1$L WHERE id = %2$L', se_beginnt_bei_geom, NEW.beginnt_bei_vp);
							EXECUTE sql;
						END IF;
					END IF;
				END IF;
				IF NOT ST_Equals(ST_EndPoint(OLD.liniengeometrie), se_endet_bei_geom) THEN
					IF debug THEN RAISE NOTICE 'Endpunkt der Linie hat sich geändert.'; END IF;
					IF NEW.endet_bei_vp IS NOT NULL THEN
						if debug THEN RAISE NOTICE 'VP am Ende des SE wurde übergeben. Frage punktgeometrie vom VP ab.'; END IF;
						sql = FORMAT('SELECT punktgeometrie FROM ukos_okstra.verbindungspunkt WHERE id = %1$L', NEW.endet_bei_vp);
						EXECUTE sql INTO vp_geom;
						if debug THEN RAISE NOTICE 'Vergleiche VP Geometrie % mit Endpunkt der Linie %', ST_AsText(vp_geom), ST_AsText(se_endet_bei_geom); END IF;
						IF NOT ST_Equals(vp_geom, se_endet_bei_geom) THEN
							if debug THEN RAISE NOTICE 'Geometrie vom VP stimmt nicht mit Ende des SE überein. Korrigiere punktgeometrie von VP am Anfang.'; END IF;
							sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET punktgeometrie = %1$L WHERE id = %2$L', se_endet_bei_geom, NEW.endet_bei_vp);
							EXECUTE sql;
						END IF;
					END IF;
				END IF;

				IF NEW.beginnt_bei_vp IS NULL OR NEW.endet_bei_vp IS NULL THEN
					if debug THEN RAISE NOTICE 'VP des Anfangs- oder Endpunktes wurde nicht übergeben. Lösche deshalb das Strassenelement.'; END IF;
					-- Lösche Strassenelement
					EXECUTE '
						DELETE FROM ukos_okstra.strassenelement
						WHERE
							gueltig_bis > $1 AND
							id = $2
					'
					USING aenderungszeit, NEW.id;

					-- Lege ein neues Strassenelement an
					if debug THEN RAISE NOTICE 'Lege neues Strassenelement an.'; END IF;
					NEW.id = uuid_generate_v4();
					NEW.geaendert_am = aenderungszeit;
					EXECUTE 'INSERT INTO ukos_okstra.strassenelement SELECT $1.*'
					USING NEW;
					RAISE NOTICE 'Neues Strassenelement mit id: % angelegt', NEW.id;
					IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NULL.', TG_NAME, TG_TABLE_NAME; END IF;
					RETURN NULL;
					-- UPDATE SE und folgende Trigger werden nicht ausgeführt
				END IF;
			END IF;

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
			RETURN NEW;
			-- Danach folgen trigger tr_after_update_update_topogeom und tr_after_update_update_sep wenn nicht ausgeschaltet
		END;
	$_$;


--
-- Name: pruefe_abhaengigkeiten(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.pruefe_abhaengigkeiten() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			aenderungszeit TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			anzahl_strassenelemente 	INTEGER;
			msg 											Text[];
			debug 										BOOLEAN = false;
		BEGIN
			-- Initialisiere
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Funktion ukos_okstra.pruefe_abhaengigkeiten in Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			IF OLD.id = '00000000-0000-0000-0000-000000000000' THEN
				RAISE EXCEPTION 'Default Verbindungspunkt id: % darf nicht gelöscht werden', OLD.id;
				RETURN NULL;
			ELSE
				-- Frage ab ob noch gültige Strassenelemente an dem Verbindungspunkt hängen
				EXECUTE '
					SELECT count(id)
					FROM ukos_okstra.strassenelement
					WHERE
						gueltig_bis > $2 AND
						(beginnt_bei_vp = $1 OR endet_bei_vp = $1)
				'
				USING OLD.id, aenderungszeit
				INTO anzahl_strassenelemente;

				IF anzahl_strassenelemente > 0 THEN
					-- es hängen noch Strassenelemente am Verbindungspunkt. Löschen abbrechen.
					IF debug THEN RAISE NOTICE 'Verbindungspunkt: % wird nicht gelöscht, weil noch % Strassenelement(e) anhängen.', OLD.ident, anzahl_strassenelemente; END IF;
					msg = array_append(msg, FORMAT('Verbindungspunkt: %1$s wird nicht gelöscht, weil noch %2$s Strassenelement(e) anhängen.', OLD.ident, anzahl_strassenelemente));
					IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NULL', TG_NAME, TG_TABLE_NAME; END IF;
					RAISE NOTICE '{ "oid" : %, "success": "false"; msg": "%"}', OLD.oid, array_to_string(msg, '<br>');
					RETURN NULL;
				ELSE
					IF debug THEN RAISE NOTICE 'Verbindungspunkt: % kann gelöscht werden, weil kein Strassenelement mehr anhängt.', OLD.ident; END IF;
					IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN OLD', TG_NAME, TG_TABLE_NAME; END IF;
					RETURN OLD;
				END IF;
			END IF;
		END;
	$_$;


--
-- Name: FUNCTION pruefe_abhaengigkeiten(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.pruefe_abhaengigkeiten() IS 'Bricht den Trigger ab, wenn die id default ist oder noch gültige Strassenelemente an dem Punkt hängen.';


--
-- Name: snap_to_net(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.snap_to_net() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			snap_point 	geometry;
			tolerance 	NUMERIC;
			accuracy 		NUMERIC;
			debug 			BOOLEAN = false;
		BEGIN
			-- Initialisierung
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Koordinatengenauigkeit' INTO accuracy;
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Funktion % in Trigger % on % aus.', 'snap_to_net', TG_NAME, TG_TABLE_NAME; END IF;

			IF TG_TABLE_NAME = 'verbindungspunkt' THEN
				NEW.punktgeometrie = ST_SnapToGrid(ukos_topo.snap_to_topo(NEW.punktgeometrie, tolerance, format('VP: %s', NEW.id)), accuracy);
				IF debug THEN RAISe NOTICE 'Snap Punkt to Grid an Position: %', ST_AsText(NEW.punktgeometrie); END IF;
			END IF;

			IF TG_TABLE_NAME = 'strassenelement' THEN
				snap_point = ukos_topo.find_snap_point(ST_StartPoint(NEW.liniengeometrie), tolerance, format('Anfangspunkt von SE: %s', NEW.id));
				IF NOT ST_Equals(ST_StartPoint(NEW.liniengeometrie), snap_point) THEN
			  	NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, 0, snap_point);
				END IF;
				snap_point = ukos_topo.find_snap_point(ST_EndPoint(NEW.liniengeometrie), tolerance, format('Endpunkt von SE: %s', NEW.id));
				IF NOT ST_Equals(ST_EndPoint(NEW.liniengeometrie), snap_point) THEN
					NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, -1, snap_point);
				END IF;
			END IF;

			IF debug THEN RAISE NOTICE 'Beende Funktion % in Trigger % on % mit RETURN NEW.', 'snap_to_net', TG_NAME, TG_TABLE_NAME; END IF;
			RETURN NEW;
		END;
	$_$;


--
-- Name: FUNCTION snap_to_net(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.snap_to_net() IS 'Snappt Verbindungspunkte oder Start- und Endpunkte von Strassenelementen auf vorhandene Verbindungspunkte im Toleranzradius. Snappt zusätzlich Start- und Endpunkte von Strassenelementen auf vorhandene Strassenelemente im Toleranzabstand, falls diese nicht vorher schon auf VP gesnappt sind.';


--
-- Name: snap_to_topology(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.snap_to_topology() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			fusspunkt RECORD;
			vp RECORD;
			tolerance NUMERIC;
			aenderungszeit	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
		BEGIN
			-- Initialisierung
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF TG_TABLE_NAME = 'verbindungspunkt' THEN
				NEW.punktgeometrie = ukos_topo.snap_to_topo(NEW.punktgeometrie, tolerance);
			END IF;
			
			IF TG_TABLE_NAME = 'strassenelement' THEN
			  NEW.liniengeometrie = ST_SetPoint(
					NEW.liniengeometrie,
					0,
					ukos_topo.snap_to_topo(ST_StartPoint(NEW.liniengeometrie), tolerance)
				);
				NEW.liniengeometrie = ST_SetPoint(
					NEW.liniengeometrie,
					ST_NumPoints(NEW.liniengeometrie) - 1,
					ukos_topo.snap_to_topo(ST_EndPoint(NEW.liniengeometrie), tolerance)
				);
			END IF;

			RETURN NEW;
		END;
	$_$;


--
-- Name: FUNCTION snap_to_topology(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.snap_to_topology() IS 'Snappt Verbindungspunkte sowie Start- und Endpunkte von Strassenelementen  auf vorhandene Verbindungspunkte im Toleranzradius. Snappt zusätzlich Start- und Endpunkte von Strassenelementen auf vorhandene Strassenelemente im Toleranzabstand, falls diese nicht vorher schon auf VP gesnappt sind.';


--
-- Name: split_strassenelemente(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.split_strassenelemente() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			tolerance				NUMERIC;
			se							RECORD;
			teil_anfang_id	CHARACTER VARYING;
			teil_ende_id		CHARACTER VARYING;
			new_se_id				CHARACTER VARYING;
			sep							RECORD;
			new_sep_id			CHARACTER VARYING;
			new_sep_station	NUMERIC;
			sql							TEXT;
			aenderungszeit	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			debug						BOOLEAN = false;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			-- Initialisierung
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			--------------------------------------------------------------------------------------------------------
			sql = FORMAT('
				SELECT
					id,
					ident,
					st_distance(%1$L, liniengeometrie),
					st_astext(liniengeometrie),
					ST_LineLocatePoint(liniengeometrie, %1$L),
					ST_LineInterpolatePoint(liniengeometrie, ST_LineLocatePoint(liniengeometrie, %1$L)) fusspunkt,
					ST_LineSubstring(liniengeometrie, 0, ST_LineLocatePoint(liniengeometrie, %1$L)) teil_anfang,
					ST_LineSubstring(liniengeometrie, ST_LineLocatePoint(liniengeometrie, %1$L), 1) teil_ende
				FROM
					ukos_okstra.strassenelement
				WHERE
					gueltig_bis > %3$L AND
					%1$L && liniengeometrie AND
					st_distance(%1$L, liniengeometrie) < %2$s AND
					ST_Length(ST_LineSubstring(liniengeometrie, 0, ST_LineLocatePoint(liniengeometrie, %1$L))) > %2$s AND
					ST_Length(ST_LineSubstring(liniengeometrie, ST_LineLocatePoint(liniengeometrie, %1$L), 1)) > %2$s
			', NEW.punktgeometrie, tolerance, aenderungszeit);
			IF debug THEN RAISE NOTICE 'Prüfe ob Verbindungspunkt Strassenelemente auftrennt mit SQL: %', sql; END IF;
			FOR se IN EXECUTE sql LOOP
				IF debug THEN RAISE NOTICE 'Trenne Strassenelement: % am Verbindungspunkt % auf mit Punktgeometrie: % und Topologietolerance: %', se.ident, NEW.ident, ST_AsText(NEW.punktgeometrie), tolerance; END IF;


				IF debug THEN RAISE NOTICE 'Setze beginnt_ und endet_bei_vp auf default, damit sie beim Löschen des SE nicht mit gelöscht werden.'; END IF;
				EXECUTE FORMAT('UPDATE ukos_okstra.strassenelement SET beginnt_bei_vp = %1$L, endet_bei_vp = %1$L WHERE id = %2$L', '00000000-0000-0000-0000-000000000000', se.id);
				IF debug THEN RAISE NOTICE 'Lösche Strassenelement: %', se.ident; END IF;
				EXECUTE FORMAT('DELETE FROM ukos_okstra.strassenelement WHERE id = %1$L', se.id);


				-- Füge das neue Strassenelement vom Anfangsteil des alten Strassenelementes ein
				sql = FORMAT('
					INSERT INTO ukos_okstra.strassenelement(
						gueltig_von, gueltig_bis,
						id_strassenelement, id_preisermittlung, 
						id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
						id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
						id_eigentuemer, id_baulasttraeger, ahk, baujahr,
						angelegt_am, angelegt_von,
						geaendert_am, geaendert_von, ident_hist, bemerkung, 
						objektname, zusatzbezeichnung,
						objektart, objektart_kurz, 
						objektnummer, zustandsnote, datum_der_benotung, pauschalpreis, 
						baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung, 
						eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand, 
						kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis, 
						hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt,
						geometrie_streckenobjekt, 
						liniengeometrie,
						gdf_id, verkehrsrichtung,
						laenge,
						stufe, erfassungsdatum, 
						systemdatum, textfeld, art_der_erfassung, art_der_erfassung_sonst, 
						quelle_der_information, quelle_der_information_sonst, rfid, migrationshinweise, 
						unscharf, hat_objekt_id, zwischen_kreuzungsbereichen, im_kreuzungsbereich, 
						hat_teilelement,
						beginnt_bei_vp,
						endet_bei_vp,
						in_nullpunkt, 
						in_komplexem_knoten)
					SELECT
						%1$L, ''2100-01-01 02:00:00+01'',
						id_strassenelement, id_preisermittlung, 
						id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
						id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
						id_eigentuemer, id_baulasttraeger, ahk, baujahr,
						%1$L, %2$L,
						geaendert_am, geaendert_von, ident_hist, bemerkung, 
						objektname, zusatzbezeichnung,
						objektart, objektart_kurz, 
						objektnummer, zustandsnote, datum_der_benotung, pauschalpreis, 
						baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung, 
						eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand, 
						kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis, 
						hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt,
						geometrie_streckenobjekt,
						%3$L,
						gdf_id, verkehrsrichtung,
						%4$s,
						stufe, erfassungsdatum, 
						systemdatum, textfeld, art_der_erfassung, art_der_erfassung_sonst, 
						quelle_der_information, quelle_der_information_sonst, rfid, migrationshinweise, 
						unscharf, hat_objekt_id, zwischen_kreuzungsbereichen, im_kreuzungsbereich, 
						hat_teilelement,
						beginnt_bei_vp,
						%5$L,
						in_nullpunkt, 
						in_komplexem_knoten
					FROM ukos_okstra.strassenelement
					WHERE id = %6$L
					RETURNING id
				', aenderungszeit, NEW.angelegt_von, se.teil_anfang, ST_Length(se.teil_anfang), NEW.id, se.id);
				EXECUTE sql INTO teil_anfang_id;
				IF debug THEN RAISE NOTICE 'Strassenelement id: % am Anfang angelegt', teil_anfang_id; END IF;

				-- Füge das neue Strassenelement vom Endteil des alten Strassenelementes ein
				EXECUTE '
					INSERT INTO ukos_okstra.strassenelement(
						gueltig_von, gueltig_bis,
						id_strassenelement, id_preisermittlung, 
						id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
						id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
						id_eigentuemer, id_baulasttraeger, ahk, baujahr,
						angelegt_am, angelegt_von,
						geaendert_am, geaendert_von, ident_hist, bemerkung, 
						objektname, zusatzbezeichnung,
						objektart, objektart_kurz, 
						objektnummer, zustandsnote, datum_der_benotung, pauschalpreis, 
						baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung, 
						eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand, 
						kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis, 
						hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt,
						geometrie_streckenobjekt, 
						liniengeometrie,
						gdf_id, verkehrsrichtung, laenge, stufe, erfassungsdatum, 
						systemdatum, textfeld, art_der_erfassung, art_der_erfassung_sonst, 
						quelle_der_information, quelle_der_information_sonst, rfid, migrationshinweise, 
						unscharf, hat_objekt_id, zwischen_kreuzungsbereichen, im_kreuzungsbereich, 
						hat_teilelement,
						beginnt_bei_vp,
						endet_bei_vp,
						in_nullpunkt, 
						in_komplexem_knoten)
					SELECT
						$1, ''2100-01-01 02:00:00+01'',
						id_strassenelement, id_preisermittlung, 
						id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
						id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
						id_eigentuemer, id_baulasttraeger, ahk, baujahr,
						$1, $2,
						geaendert_am, geaendert_von, ident_hist, bemerkung, 
						objektname, zusatzbezeichnung,
						objektart, objektart_kurz, 
						objektnummer, zustandsnote, datum_der_benotung, pauschalpreis, 
						baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung, 
						eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand, 
						kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis, 
						hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt,
						geometrie_streckenobjekt,
						$3,
						gdf_id, verkehrsrichtung,
						$4,
						stufe, erfassungsdatum, 
						systemdatum, textfeld, art_der_erfassung, art_der_erfassung_sonst, 
						quelle_der_information, quelle_der_information_sonst, rfid, migrationshinweise, 
						unscharf, hat_objekt_id, zwischen_kreuzungsbereichen, im_kreuzungsbereich, 
						hat_teilelement,
						$5,
						endet_bei_vp,
						in_nullpunkt, 
						in_komplexem_knoten
					FROM ukos_okstra.strassenelement
					WHERE id = $6
					RETURNING id
				'
				USING aenderungszeit, NEW.angelegt_von, se.teil_ende, ST_Length(se.teil_ende), NEW.id, se.id
				INTO teil_ende_id;
				IF debug THEN RAISE NOTICE 'Strassenelement id: % am Ende angelegt', teil_ende_id; END IF;

				-- Passe die Strassenelementpunkte des alten Strassenelementes an
				FOR sep IN EXECUTE '
					SELECT
						id,
						station,
						abstand_zur_bestandsachse
					FROM
						ukos_okstra.strassenelementpunkt
					WHERE
						gueltig_bis > $2 AND
						auf_strassenelement = $1
					ORDER BY
						station, abstand_zur_bestandsachse
				'
				USING se.id, aenderungszeit
				LOOP
					IF sep.station < ST_Length(se.teil_anfang) THEN
						IF debug THEN RAISE NOTICE 'Erzeuge und ordne Strassenelementpunkt % dem Anfangsteil % zu,', sep.id, teil_anfang_id; END IF;
						new_se_id = teil_anfang_id;
						new_sep_station = sep.station;
					ELSE
						IF debug THEN RAISE NOTICE 'Erzeuge und ordne Strassenelementpunkt % dem Endteil % zu.', se.id, teil_ende_id; END IF;
						new_sep_station = sep.station - ST_Length(se.teil_anfang);
						new_se_id = teil_ende_id;
					END IF;

					-- Setze den alten Strassenelementpunkt auf historisch
					EXECUTE 'UPDATE ukos_okstra.strassenelementpunkt SET gueltig_bis = $2 WHERE id = $1'
					USING sep.id, aenderungszeit;

					-- Füge den neuen Strassenelementpunkt ein
					EXECUTE '
						INSERT INTO ukos_okstra.strassenelementpunkt(
							gueltig_von, gueltig_bis,
							id_strassenelement,
							id_preisermittlung, 
							id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
							id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
							id_eigentuemer, id_baulasttraeger, ahk, baujahr,
							angelegt_am, angelegt_von,
							geaendert_am, geaendert_von,
							ident_hist, bemerkung, 
							objektname, zusatzbezeichnung, objektart, objektart_kurz, 
							objektnummer, zustandsnote, datum_der_benotung, pauschalpreis, 
							baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung, 
							eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand, 
							kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis, 
							hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt, punktgeometrie,
							station,
							abstand_zur_bestandsachse,
							abstand_zur_fahrbahnoberkante,
							auf_strassenelement,
							id_strasse
						)
						SELECT
							$1, ''2100-01-01 02:00:00+01'',
							$2,
							id_preisermittlung, 
							id_zustand, id_zustandsbewertung_01, id_zustandsbewertung_02, 
							id_zustandsbewertung_03, id_zustandsbewertung_04, id_zustandsbewertung_05, 
							id_eigentuemer, id_baulasttraeger, ahk, baujahr,
							$1, $3,
							geaendert_am, geaendert_von, ident_hist, bemerkung,
							objektname, zusatzbezeichnung, objektart, objektart_kurz,
							objektnummer, zustandsnote, datum_der_benotung, pauschalpreis,
							baulasttraeger, baulasttraeger_dritter, abschreibung, art_der_preisermittlung,
							eroeffnungsbilanzwert, zeitwert, fremdobjekt, fremddatenbestand,
							kommunikationsobjekt, erzeugt_von_ereignis, geloescht_von_ereignis,
							hat_vorgaenger_hist_objekt, hat_nachfolger_hist_objekt, punktgeometrie,
							$5,
							abstand_zur_bestandsachse, abstand_zur_fahrbahnoberkante,
							$2,
							id_strasse
						FROM ukos_okstra.strassenelementpunkt
						WHERE id = $4
						RETURNING id
					'
					USING aenderungszeit, new_se_id, NEW.angelegt_von, sep.id, new_sep_station
					INTO new_sep_id;

					IF debug THEN RAISE NOTICE 'Neuen Strassenelementpunkt % mit Station % und Abstand: % angelegt.', new_sep_id, new_sep_station, sep.abstand_zur_bestandsachse; END IF;

				END LOOP;

			END LOOP;
			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW', TG_NAME, TG_TABLE_NAME; END IF;
			RETURN NEW;
		END;
	$_$;


--
-- Name: stop(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.stop() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			loeschsperre 	BOOLEAN;
			debug					BOOLEAN = false;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
			IF debug THEN RAISE NOTICE 'Führe Funktion ukos_okstra.stop in Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			IF loeschsperre THEN
				IF debug THEN RAISE NOTICE 'Echtes Löschen von Objekt id: % gestoppt', OLD.id; END IF;
				RAISE NOTICE 'success';
				IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NULL', TG_NAME, TG_TABLE_NAME; END IF;
				RETURN NULL;
			ELSE
				IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN OLD', TG_NAME, TG_TABLE_NAME; END IF;
				RETURN OLD;
			END IF;
		END;
	$_$;


--
-- Name: FUNCTION stop(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.stop() IS 'Verhindert die Ausführung des Statements, wenn der Parameter Löschsperre auf true gesetzt ist.';


--
-- Name: untergang(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.untergang() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
			DECLARE
				aenderungszeit 	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
				loeschsperre 		BOOLEAN;
				debug 					BOOLEAN = false;
			BEGIN
				-- Initialisieren
				EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
				EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
				IF debug THEN RAISE NOTICE 'Führe Funktion ukos_okstra.untergang in Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

				IF loeschsperre AND OLD.gueltig_bis > aenderungszeit THEN
					EXECUTE '
						UPDATE
							' || TG_TABLE_SCHEMA || '.' || TG_TABLE_NAME || '
						SET
							gueltig_bis = $2
						WHERE
							id = $1
					'
					USING OLD.id, aenderungszeit;
					IF debug THEN RAISE NOTICE '%.% id: % nicht mehr gültig seit: %', TG_TABLE_SCHEMA, TG_TABLE_NAME, OLD.id, OLD.gueltig_bis; END IF;
				END IF;

				IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN OLD', TG_NAME, TG_TABLE_NAME; END IF;
				RETURN OLD;
			END;
		$_$;


--
-- Name: FUNCTION untergang(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.untergang() IS 'Setzt die Gültigkeit des Datensatzes OLD.id in der Tabelle in der das Event auftritt auf das aktuelle Datum und läßt ihn damit untergehen, wenn der Parameter Löschsperre auf true gesetzt ist. Der Rückgabewert ist OLD, damit danach auch noch Trigger ausgeführt werden können.';


--
-- Name: update_querschnittstreifen(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.update_querschnittstreifen() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			sql text;

		BEGIN
			-- querschnittstreifen löschen
			-- dabei wird ggf. auch das Teilelement gelöscht,
			-- sowie ggf. auch Strassenelementpunkte
			sql = FORMAT('
				DELETE FROM
					ukos_okstra.querschnittstreifen
				WHERE
					oid = %1$L
				',
				NEW.oid
			);
			EXECUTE sql;

			-- Neu anlegen des Querschnitttrapez
			sql = FORMAT('
				INSERT INTO ukos_okstra.querschnitttrapeze (
					in_verkehrsflaeche,
					strassenelement_id,
					beginnt_bei_station,
					x_wert_von_station_links,
					x_wert_von_station_rechts,
					endet_bei_station,
					x_wert_bis_station_links,
					x_wert_bis_station_rechts
				)
				VALUES (
					%1$L,
					%2$L,
					%3$L,
					%4$L,
					%5L,
					%6$L,
					%7$L,
					%8$L
				)',
				NEW.in_verkehrsflaeche,
				NEW.strassenelement_id,
				NEW.beginnt_bei_station,
				NEW.x_wert_von_station_links,
				NEW.x_wert_von_station_rechts,
				NEW.endet_bei_station,
				NEW.x_wert_bis_station_links,
				NEW.x_wert_bis_station_rechts
			);
			EXECUTE sql;
			RETURN NEW;
		END;
	$_$;


--
-- Name: update_sep(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.update_sep() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			sep							RECORD;
			aenderungszeit	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			debug 					BOOLEAN = false;
		BEGIN
			-- Initialisieren
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

			FOR sep IN EXECUTE '
				SELECT
					id
				FROM
					ukos_okstra.strassenelementpunkt
				WHERE
					gueltig_bis > $1 AND
					auf_strassenelement = $2
				'
			USING aenderungszeit, OLD.id
			LOOP
				IF debug THEN RAISE NOTICE 'Ordne Strassenelementpunkt id: % neu zu dichtestem Strassenelement zu.', sep.id; END IF;
				EXECUTE '
					UPDATE ukos_okstra.strassenelementpunkt
					SET
						auf_strassenelement = NULL,
						abstand_zur_bestandsachse = NULL,
						station = NULL
					WHERE
						id = $1
				'
				USING sep.id;
			END LOOP;
			IF debug THEN RAISE NOTICE 'Anhängende Strassenelementpunkte neu zugeordnet.'; END IF;

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
			RETURN NEW;
		END;
	$_$;


--
-- Name: update_topogeom(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.update_topogeom() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		BEGIN
			--------------------------------------------------------------------------------------------------------
			EXECUTE '
				SELECT
					topology.ST_ChangeEdgeGeom(
						' || quote_literal('ukos_topo') || ',
						(
							SELECT
								e[1]
							FROM
								(
									SELECT
										topology.GetTopoGeomElements(liniengeometrie_topo) e
									FROM
										ukos_okstra.strassenelement
									WHERE
										id = $1
									LIMIT 1
								) e_tab
						),
						$2
					)
			'
			USING NEW.id, NEW.liniengeometrie;

			RETURN NEW;
		END;
	$_$;


--
-- Name: FUNCTION update_topogeom(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION ukos_okstra.update_topogeom() IS 'Aktualisiert die Liniengeometrie der Edge des Strassenelements in die Topologie.';


--
-- Name: update_with_sep(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.update_with_sep() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			rec								RECORD;
			aenderungszeit		TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			tolerance 				NUMERIC;
			sql								TEXT;
      se_id							CHARACTER VARYING;
      sep 	 						RECORD;
			num_sap 					integer;
			updated						BOOLEAN = false;
			ident 						character varying;
			target_table_name character varying;
			target_columns    character VARYING[];
      msg								TEXT[];
			oid								bigint;
			debug 						BOOLEAN = false;
		BEGIN

			-- Initialisieren
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

			IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;
			EXECUTE FORMAT('
				SELECT SUBSTRING(%1$L, 5)
			', TG_TABLE_NAME)
			INTO target_table_name;

			-- Im Updatefall geht punktgeometrie vor auf_strassenelement_id, damit man den SAP auch mit der Geometrie einem anderen SEP zuordnen oder die Geometrie des aktuellen verschieben kann
			-- Wenn es an der neuen Position schon einen SEP gibt wird einfach dieser genommen.
			-- ansonsten wird geprüft, ob der alte SEP verschoben oder ein neuer angelegt werden soll.
			-- Der alte kann nur verschoben werden, wenn ein bei_sep_id angegeben wurde und wenn am alten sep
			-- weniger als 2 Ausstattungspunkte hängen.

			-- Finde heraus ob an punktgeometrie schon ein SEP existiert
			sql = FORMAT('
				SELECT
					id, ident
				FROM
					ukos_okstra.strassenelementpunkt
				WHERE ST_DWithin(punktgeometrie, %1$L, %2$s)
				LIMIT 1
			', NEW.punktgeometrie, tolerance);
			IF debug THEN RAISe NOTICE 'Suche SEP mit Sql: %', sql; END IF;
			EXECUTE sql INTO sep;

			IF (sep IS NULL) THEN
				IF debug THEN RAISE NOTICE 'Kein SEP an neuer Position gefunden'; END IF;
				-- Wenn letzer bei_strassenelementpunkt_id nur diesen SAP anhängig hat, die punktgeometrie dieses SEP verschieben auf neue Position.
				IF (NEW.bei_strassenelementpunkt_id IS NOT NULL) THEN
					IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id wurde mit übergeben.'; END IF;
					sql = FORMAT('
						SELECT
							count(bei_strassenelementpunkt_id)
						FROM
							ukos_okstra.strassenausstattung_punkt
						WHERE
							gueltig_bis > %1$L AND
							bei_strassenelementpunkt_id = %2$L
					', aenderungszeit, NEW.bei_strassenelementpunkt_id);
					EXECUTE sql
					INTO num_sap;
					IF (num_sap < 2) THEN
						IF debug THEN RAISE NOTICE 'Es hängt keiner oder nur ein SAP an dem SEP: %.', NEW.bei_strassenelementpunkt_id; END IF;
						sql = FORMAT('
							UPDATE
								ukos_okstra.strassenelementpunkt
							SET
								punktgeometrie = %2$L
							WHERE
								id = %1$L
						', NEW.bei_strassenelementpunkt_id, NEW.punktgeometrie);
						IF debug THEN RAISE NOTICE 'SEP: % verschieben auf neue Position: % mit sql: %.', NEW.bei_strassenelementpunkt_id, ST_AsText(NEW.punktgeometrie), sql; END IF;
						msg = array_append(msg, FORMAT('Strassenelementpunkt %1$s auf neue Position verschoben.', NEW.bei_strassenelementpunkt_id));
						EXECUTE sql;
						updated = true;
					ELSE
						IF debug THEN RAISE NOTICE 'Es hängen mehr als 1 SAP an SEP: %', NEW.bei_strassenelementpunkt_id; END IF;
					END IF;
				ELSE
					IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id wurde nicht übergeben!'; END IF;
				END IF;

				IF (NOT updated) THEN
					sql = FORMAT('
						INSERT INTO ukos_okstra.strassenelementpunkt (punktgeometrie)
						VALUES (%1$L)
						RETURNING id
					', NEW.punktgeometrie);
					EXECUTE sql
					INTO NEW.bei_strassenelementpunkt_id;
					IF debug THEN RAISe NOTICE 'Neuen SEP mit id: % angelegt.', NEW.bei_strassenelementpunkt_id; END IF;
					msg = array_append(msg, FORMAT('Neuen Strassenelementpunkt %1$s erzeugt.', NEW.bei_strassenelementpunkt_id));
				ELSE
					IF debug THEN RAISe NOTICE 'SEP mit ident: % gefunden', sep.ident; END IF;
					msg = array_append(msg, FORMAT('Objekt fällt auf Strassenelementpunkt %1$s.', sep.ident));
					NEW.bei_strassenelementpunkt_id = sep.id;
				END IF;
			ELSE
				IF debug THEN RAISe NOTICE 'SEP: % id: (%) an Punktgeometrie: % gefunden.', sep.ident, sep.id, ST_AsText(NEW.punktgeometrie); END IF;
				msg = array_append(msg, FORMAT('Objekt fällt auf Strassenelementpunkt %1$s.', sep.ident));
				NEW.bei_strassenelementpunkt_id = sep.id;
			END IF;

			-- Führe den UPDATE Befehl on SAP aus!
			sql = FORMAT('
				SELECT
					column_name
				FROM
					information_schema.columns
				WHERE
					table_schema = %1$L AND
					table_name = %2$L AND
					column_name NOT IN (''id'')
				ORDER BY
					column_name
			', TG_TABLE_SCHEMA, target_table_name);
			IF debug THEN RAISe NOTICE 'Frage alle Attribute der Doppik-Tabelle: %.% ab mit sql: %', TG_TABLE_SCHEMA, target_table_name, sql; END IF;
			IF debug THEN RAISe NOTICE 'Update Datensatz % in Tabelle: %.% mit folgenden Attributen', NEW.ident, TG_TABLE_SCHEMA, target_table_name; END IF;
			FOR rec IN EXECUTE sql
			LOOP
				--IF debug THEN RAISe NOTICE '%: %', rec.column_name, hstore(NEW)->rec.column_name; END IF;
				IF (hstore(NEW)->rec.column_name) IS NOT NULL THEN
					IF debug THEN RAISe NOTICE '%: %', rec.column_name, hstore(NEW)->rec.column_name; END IF;
					target_columns = array_append(target_columns, FORMAT('%1$I = %2$L', rec.column_name::character VARYING, hstore(NEW)->rec.column_name)::character varying);
				END IF;
      END LOOP;

			sql = FORMAT('
				UPDATE %1$I.%2$I
				SET %3$s
				WHERE id = %4$L
			', TG_TABLE_SCHEMA, target_table_name, array_to_string(target_columns, ', '), NEW.id);
			IF debug THEN RAISE NOTICE 'Sql zum UDATE des Objektes: %', sql; END IF;
			EXECUTE sql;
			msg = array_append(msg, FORMAT('Punktobjekt %1$s geupdated.', NEW.ident));

			IF OLD.bei_strassenelementpunkt_id != NEW.bei_strassenelementpunkt_id THEN
				sql = FORMAT('
					DELETE FROM
						ukos_okstra.strassenelementpunkt
					WHERE
						id = %1$L
				', OLD.bei_strassenelementpunkt_id);
				IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id hat sich geändert. Versuche Löschung des alten SEP mit sql: %', sql; END IF;
				EXECUTE sql;
			END IF;

			IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
			RAISE NOTICE '{ "oid" : %, "msg": "%"}', NEW.oid, array_to_string(msg, '<br>');
			RETURN NEW;
		END;
	$_$;


