-- Name: find_snap_point(public.geometry, double precision, character varying); Type: FUNCTION; Schema: ukos_topo; Owner: -
--

CREATE FUNCTION ukos_topo.find_snap_point(point public.geometry, tolerance double precision, label character varying) RETURNS public.geometry
    LANGUAGE plpgsql STRICT
    AS $_$
	DECLARE
		vp RECORD;
		se RECORD;
		snap_point geometry;
		current_query text = current_query();
		aenderungszeit	TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
	BEGIN
		EXECUTE '
			SELECT
				id,
				punktgeometrie
			FROM
				ukos_okstra.verbindungspunkt
			WHERE
				gueltig_bis > $1 AND
				ST_DWithin(punktgeometrie, $2, $3)
			LIMIT 1
		'
		USING aenderungszeit, point, tolerance
		INTO vp;
		IF vp IS NULL THEN
			EXECUTE '
				SELECT
					id,
					ST_LineInterpolatePoint(liniengeometrie, ST_LineLocatePoint(liniengeometrie, $2)) AS fusspunkt
				FROM
					ukos_okstra.strassenelement
				WHERE
					gueltig_bis > $1 AND
					ST_DWithin(liniengeometrie, $2, $3)
				LIMIT 1
			'
			USING aenderungszeit, point, tolerance
			INTO se;
			IF se IS NULL THEN
				snap_point = point;
			ELSE
				snap_point = se.fusspunkt;
				PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf Fusspunkt: %s von SE: %s', label, ST_AsText(snap_point), se.id));
			END IF;
		ELSE
			snap_point = vp.punktgeometrie;
			PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf VP: %s', label, vp.id));
		END IF;
		RETURN snap_point;
	END;
	$_$;


