-- Name: snap_to_topo(public.geometry, double precision, character varying); Type: FUNCTION; Schema: ukos_topo; Owner: -
--

CREATE FUNCTION ukos_topo.snap_to_topo(point public.geometry, tolerance double precision, label character varying) RETURNS public.geometry
    LANGUAGE plpgsql STRICT
    AS $_$
			DECLARE
				snap_point 			geometry;
				current_query 	text = current_query();
				debug 					BOOLEAN = false;
			BEGIN

				-- Initialisierung
				EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
				IF debug THEN RAISE NOTICE 'Führe Funktion % aus.', 'ukos_topo.snap_to_topo'; END IF;

				EXECUTE '
					SELECT geom
					FROM ukos_topo.node
					WHERE ST_DWithin(geom, $1, $2)
				'
				USING point, tolerance
				INTO snap_point;
				IF snap_point IS NULL THEN
					IF debug THEN RAISE NOTICE 'Kein anderer Node in der Toleranz des neuen Punktes.'; END IF;
					EXECUTE '
						SELECT ST_LineInterpolatePoint(geom, ST_LineLocatePoint(geom, $1))
						FROM ukos_topo.edge_data
						WHERE ST_DWithin(geom, $1, $2)
						LIMIT 1
					'
					USING point, tolerance
					INTO snap_point;
					IF snap_point IS NULL THEN
						IF debug THEN RAISE NOTICE 'Kein SE in der Toleranz des neuen Punktes.'; END IF;
						snap_point = point;
					ELSE
						IF debug THEN RAISE NOTICE 'Snap Punkt auf Strassenelement an Lotfußpunkt: %', ST_AsText(snap_point); END IF;
						PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf Fusspunkt %s', label, ST_AsText(snap_point)));
					END IF;
				ELSE
					IF debug THEN RAISE NOTICE 'Snap Punkt auf Node an Position: %', ST_AsText(snap_point); END IF;
					PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf Node %s', label, ST_AsText(snap_point)));
				END IF;

				IF debug THEN RAISE NOTICE 'Beende Funktion %.', 'ukos_topo.snap_to_topo'; END IF;
				RETURN snap_point;
			END;
			$_$;


